import React, { Component } from "react";

class TodoForm extends Component{

  state = {
    text : ""
  };

  Change_handle(event)
  {
    this.setState = ({
      [event.target.name] : event.target.value
    });
  };

  render(){
    return (
      <input name = "text" type = "text" placeholder="todo....." value = {this.state.text} onChange = {this.Change_handle.bind(this)} />
    );
  }
}

export default TodoForm;
