import React, { Component } from "react";
import TodoForm from "../components/TodoForm"


class Todo extends Component{

  state = {
    todos : []
  };

  addTodo(todo)
  {
    this.setState({
      todos : [todo, ...this.state.todos]
    });
  }

  showTodo()
  {
    this.setState({
      todos : this.state.todos
    });
  }

  render(){
    return (
      <div>
        <center>
          <h1>Todo List</h1>
          <TodoForm onClick = {this.addTodo.bind(this)} />
        </center>
        <div>
          <center>
            <h2>Following Things are there in ToDo List :</h2>
            <TextArea />
          </center>
        </div>
      </div>
    );
  }

}

export default Todo;
